mapper.select:
select * from (
select id, card_id, bank_card_id, transaction_date, bookkeeping_date, transaction_desc, balance_currency,
balance_money, card_type_id, card_type_name, consumption_type, consume_id, consume_name, demoarea
from credit where 1 = 1


        and transaction_date >= #{credit.transactionDateStart,jdbcType=DATE}


        and transaction_date <= #{credit.transactionDateEnd,jdbcType=DATE}


        and consumption_type = #{credit.consumptionType,jdbcType=NUMERIC}


        and bank_card_id = #{credit.bankCardId,jdbcType=VARCHAR}


        and card_type_name = #{credit.cardTypeName,jdbcType=VARCHAR}


        and consume_id in

            #{consumeID,jdbcType=VARCHAR}



        and consume_name = #{credit.consumeName,jdbcType=VARCHAR}


        and (
            demoarea like CONCAT('%', #{credit.demoArea,jdbcType=VARCHAR}, '%')
            or transaction_desc like CONCAT('%', #{credit.demoArea,jdbcType=VARCHAR}, '%')
        )


        and (dayofweek(transaction_date)-1) = #{credit.weekName,jdbcType=VARCHAR}


        and year(transaction_date) = #{credit.year,jdbcType=VARCHAR}


        and month(transaction_date) = #{credit.month,jdbcType=VARCHAR}


order by transaction_date desc
) t limit #{page.recordBegin,jdbcType=NUMERIC}, #{page.pageSize,jdbcType=NUMERIC}

select count(id) from credit
where 1 = 1


        and transaction_date >= #{credit.transactionDateStart,jdbcType=DATE}


        and transaction_date <= #{credit.transactionDateEnd,jdbcType=DATE}


        and consumption_type = #{credit.consumptionType,jdbcType=NUMERIC}


        and bank_card_id = #{credit.bankCardId,jdbcType=VARCHAR}


        and card_type_name = #{credit.cardTypeName,jdbcType=VARCHAR}


        and consume_id in

            #{consumeID,jdbcType=VARCHAR}



        and consume_name = #{credit.consumeName,jdbcType=VARCHAR}


        and (demoarea like '%'||#{credit.demoArea,jdbcType=VARCHAR}||'%'
        or transaction_desc like '%'||#{credit.demoArea,jdbcType=VARCHAR}||'%' )


        and (dayofweek(transaction_date) - 1) = #{credit.weekName,jdbcType=VARCHAR}


        and year(transaction_date) = #{credit.year,jdbcType=VARCHAR}


        and month(transaction_date) = #{credit.month,jdbcType=VARCHAR}

select round(balance_money) 'value', case when (isnull(consume_name)=1 || length(trim(consume_name)) =0) then 'unkown' else consume_name end 'key' from (select sum(balance_money) balance_money, consume_id, consume_name from credit where 1 = 1 and consumption_type = 1
and transaction_date >= #{credit.transactionDateStart,jdbcType=DATE}
and transaction_date <= #{credit.transactionDateEnd,jdbcType=DATE}
and card_type_name = #{credit.cardTypeName,jdbcType=VARCHAR}
and consume_id in
#{consumeID,jdbcType=VARCHAR}
and (demoarea like '%'||#{credit.demoArea,jdbcType=VARCHAR}||'%' or transaction_desc like '%'||#{credit.demoArea,jdbcType=VARCHAR}||'%' )
group by consume_id, consume_name) t order by consume_id

select round(sum(balance_money)) 'value', transaction_date 'key' from (
select dayofweek(transaction_date)-1 transaction_date, balance_money
from credit
where consumption_type = 1


        and transaction_date >= #{credit.transactionDateStart,jdbcType=DATE}


        and transaction_date <= #{credit.transactionDateEnd,jdbcType=DATE}


        and card_type_name = #{credit.cardTypeName,jdbcType=VARCHAR}


        and consume_id in

            #{consumeID,jdbcType=VARCHAR}



        and (demoarea like '%'||#{credit.demoArea,jdbcType=VARCHAR}||'%'
        or transaction_desc like '%'||#{credit.demoArea,jdbcType=VARCHAR}||'%' )


) view1
group by transaction_date
order by transaction_date

select round(balance_money) 'value', case when cmonth = 1 then 'Janunary' when cmonth = 2 then 'February' when cmonth = 3 then 'March' when cmonth = 4 then 'April' when cmonth = 5 then 'May' when cmonth = 6 then 'June' when cmonth = 7 then 'July' when cmonth = 8 then 'August' when cmonth = 9 then 'September' when cmonth = 10 then 'October' when cmonth = 11 then 'November' when cmonth = 12 then 'December' else cmonth end 'key' from (select sum(balance_money) balance_money,cmonth from (select balance_money, month(transaction_date) cmonth from credit where 1 = 1 and consumption_type = 1
and year(transaction_date) = #{credit.year,jdbcType=VARCHAR}
and card_type_name = #{credit.cardTypeName,jdbcType=VARCHAR}
and consume_id in
#{consumeID,jdbcType=VARCHAR}
and (demoarea like '%'||#{credit.demoArea,jdbcType=VARCHAR}||'%' or transaction_desc like '%'||#{credit.demoArea,jdbcType=VARCHAR}||'%' )
) t1 group by cmonth) t2 order by cmonth
